import React, { useState, useCallback } from 'react';
import { Input } from 'antd';
import { useSelector, useDispatch } from "react-redux";
import PropTypes from 'prop-types';
import { Button, Card, Avatar } from 'antd';
import { HeartOutlined , MessageOutlined, MenuOutlined, UserAddOutlined, UserOutlined } from "@ant-design/icons";

const MainPost = ({ post }) => {
    const [commentFormOpen, setCommentFormOpen] = useState(false);
    const [commentText, setCommentText] = useState('');

    const onClickComment = useCallback(() => {
        if(commentFormOpen) {
            setCommentFormOpen(false);
        } else {
            setCommentFormOpen(true);
        }

        console.log(commentFormOpen);
    }, [commentFormOpen]);

      return (
          <>
              <Card
                  key={post}
                  actions={[
                    <div key="heart"><HeartOutlined /></div>,
                    <div key="comment" onClick={onClickComment}><MessageOutlined /></div>,
                    <div key="else"><MenuOutlined /></div>,
                  ]}
                  cover={<img alt="example" src={post.testImg}
                              style={{ maxHeight: 500}}
                  />}
              >
                <Button style={{ float: 'right' }}><UserAddOutlined /></Button>
                <Card.Meta
                    title={post.User.nickname}
                    avatar={<Avatar size={30} icon={<UserOutlined/>}/>}
                    description={post.content}
                />
              </Card>
              {commentFormOpen && (
                  <>
                      <Input.Search placeholder="input text" onSearch={value => console.log(value)} />
                  </>
              )}
              <br />
          </>
      );
};

MainPost.PropTypes = {
    post: PropTypes.node,
};

export default MainPost;
