import React from 'react';
import { UserOutlined, HeartOutlined } from '@ant-design/icons';
import { Input, Button, Form, Card, Avatar } from 'antd';

const testPost = {
    nickname : 'seoyoung'

};

// 타임라인 역할
const Home = () => {
    return (
        <>
            <div style={{ padding: 10 }}>Hello, FRIEND!</div>
            <Form>
                <Input.TextArea
                    placeholder="오늘의 이야기를 적어보세요"
                    maxLength={140}
                    autoSize={{ minRows: 4, maxRows: 4 }}
                />
                <div style={{ padding: 10}}>
                    <Button >Image</Button>
                    <Button type="primary" style={{ float : 'right'}}>Post</Button>
                </div>
            </Form>

            <Card
                actions={[
                    <div><HeartOutlined /></div>,
                    <div></div>,
                    <div></div>,
                ]}
            >
                <Card.Meta
                    title={testPost.nickname}
                    avatar={<Avatar size={30} icon={<UserOutlined/>}/>}/>

            </Card>
        </>
    );
};

export default Home;
