import React , { useState } from 'react';
import Head from 'next/Head';
import { Form , Button, Input, Checkbox } from 'antd';
import AppLayout from '../components/AppLayout';

const Signup = () => {

    const [id, setId] = useState('');
    const [nickname, setNickname] = useState('');
    const [password, serPassword] = useState('');
    const [term, setTerm] = useState(false);

    const onChangeId = (e) => {
        setId(e.target.value);
    }

    const onChangeNickName = (e) => {
        setNickname(e.target.value);
    }

    const onChangePassword = (e) => {
        setPassword(e.target.value);
    }

    const onChangeTerm = (e) => {
        setTerm(e.target.value);
    }

    return (
        <>
            <Head>
                <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/antd/3.18.1/antd.css" />
            </Head>
            <AppLayout>
                <div>
                    <Form style={{ padding : 20 }}>
                        <div>
                            <label>ID</label>
                            <Input name="userId" required onChange={onChangeId}/>
                        </div>
                        <div>
                            <label>NickName</label>
                            <Input name="userNickname" required onChange={onChangeNickName}/>
                        </div>
                        <div>
                            <label>Password</label>
                            <Input name="userPassword" required onChange={onChangePassword}/>
                        </div>
                        <br/>
                        <div>
                            <Checkbox name="userTerm" required onChange={onChangeTerm}>약관에 동의합니다.</Checkbox>
                        </div>
                        <br/>
                        <div>
                            <Button type="primary">Send</Button>
                        </div>
                    </Form>
                </div>
            </AppLayout>
        </>
    );
};

export default Signup;
